                owners = JSON.parse(owners).map(owner => `${owner.name} (${owner.email})`).join("\n")
                out.push({
                    "Group Name": group.name,
                    "Group ID": group.id,
                    "Owners": owners,
                    "Cursor": group.cursor
                })

            } else {
                out.push({
                    "Group Name": group.name,
                    "Group ID": group.id,
                    "Owners": "",
                    "Cursor": group.cursor
                })
            }
        } else {
            out.push({
                "Group Name": group.name,
                "Group ID": group.id,
                "Owners": "",
                "Cursor": group.cursor
            })

        }
    }
}

return {out, pageInfo}

